import React from 'react'
import Head from 'next/head'
import Header from './Header'

const Layout: React.FC<React.PropsWithChildren> = ({ children }) => {
  return (
    <div className="min-h-screen flex flex-col">
      <Head>
        <title>MRAZOTA</title>
        <meta name="description" content="Mrazota official site" />
        {process.env.NEXT_PUBLIC_RECAPTCHA_SITE_KEY && (
          <script
            // inject site key to window for client-side code without referencing process in client files
            dangerouslySetInnerHTML={{ __html: `window.__RECAPTCHA_SITE_KEY = "${process.env.NEXT_PUBLIC_RECAPTCHA_SITE_KEY}";` }}
          />
        )}
      </Head>
      <Header />
      <main className="flex-1 container py-8">{children}</main>
      <footer className="bg-white border-t mt-8 py-6">
        <div className="container text-center text-sm text-gray-500">© {new Date().getFullYear()} Mrazota</div>
      </footer>
    </div>
  )
}

export default Layout

