import { useEffect, useState } from 'react'
import io from 'socket.io-client'

let socket: any

export default function TicTacToe({ roomId }: { roomId?: string }) {
  const [board, setBoard] = useState(Array(9).fill(null))
  const [player, setPlayer] = useState<string | null>(null)

  useEffect(() => {
    socket = io(undefined, { path: '/api/socket' })
    socket.on('connect', () => console.log('tic connected'))
    socket.on('update', (state: unknown) => {
      if (state && typeof state === 'object' && 'board' in (state as any)) {
        setBoard((state as any).board)
      }
    })
    socket.on('assign', (role: string) => setPlayer(role))
    if (roomId) socket.emit('join-room', roomId)
    return () => socket.disconnect()
  }, [roomId])

  function move(i: number) {
    socket.emit('move', { i })
  }

  return (
    <div>
      <div className="grid grid-cols-3 gap-1 w-72">
        {board.map((c, i) => (
          <div key={i} className="h-20 flex items-center justify-center bg-white rounded shadow" onClick={() => move(i)}>{c}</div>
        ))}
      </div>
      <div className="mt-2">You: {player}</div>
    </div>
  )
}

import bcrypt from 'bcryptjs'
import jwt from 'jsonwebtoken'

const JWT_SECRET = process.env.JWT_SECRET || 'devsecret'

export async function hashPassword(password: string) {
  return bcrypt.hash(password, 10)
}

export async function verifyPassword(password: string, hash: string) {
  return bcrypt.compare(password, hash)
}

export function signToken(payload: object) {
  return jwt.sign(payload, JWT_SECRET, { expiresIn: '7d' })
}

export function verifyToken(token: string) {
  return jwt.verify(token, JWT_SECRET)
}

import crypto from 'crypto'

const ALGO = 'aes-256-gcm'

export function encrypt(text: string, key: string) {
  const iv = crypto.randomBytes(12)
  const cipher = crypto.createCipheriv(ALGO, Buffer.from(key, 'base64'), iv)
  const encrypted = Buffer.concat([cipher.update(text, 'utf8'), cipher.final()])
  const tag = cipher.getAuthTag()
  return Buffer.concat([iv, tag, encrypted]).toString('base64')
}

export function decrypt(data: string, key: string) {
  const input = Buffer.from(data, 'base64')
  const iv = input.slice(0, 12)
  const tag = input.slice(12, 28)
  const encrypted = input.slice(28)
  const decipher = crypto.createDecipheriv(ALGO, Buffer.from(key, 'base64'), iv)
  decipher.setAuthTag(tag)
  const out = Buffer.concat([decipher.update(encrypted), decipher.final()])
  return out.toString('utf8')
}

import crypto from 'crypto'

type Algo = 'hmac-sha256' | 'hmac-sha1' | 'md5'

export function signPayload(payload: string, secret: string, algo: Algo = 'hmac-sha256') {
  if (!secret) return ''
  if (algo === 'md5') {
    return crypto.createHash('md5').update(payload).digest('hex')
  }
  const algoName = algo === 'hmac-sha1' ? 'sha1' : 'sha256'
  return crypto.createHmac(algoName, secret).update(payload).digest('hex')
}

export function verifySignature(payload: string, received: string, secret: string, algo: Algo = 'hmac-sha256') {
  if (!received) return false
  const expected = signPayload(payload, secret, algo)
  // Use timing-safe compare
  const a = Buffer.from(expected, 'utf8')
  const b = Buffer.from(received, 'utf8')
  if (a.length !== b.length) return false
  return crypto.timingSafeEqual(a, b)
}

const paysera = { signPayload, verifySignature }

export default paysera

