import { useState } from 'react'
import useSWR from 'swr'
import axios from 'axios'

const fetcher = (url: string) => axios.get(url).then(r => r.data)

export default function AdminProducts() {
  const { data, mutate } = useSWR('/api/products', fetcher)
  const [name, setName] = useState('')
  const [price, setPrice] = useState('0')
  const [sku, setSku] = useState('')

  const [editingId, setEditingId] = useState<string | null>(null)

  async function create(e: any) {
    e.preventDefault()
    await axios.post('/api/products/create', { name, price, sku })
    setName('')
    setPrice('0')
    setSku('')
    mutate()
  }

  async function saveEdit(e: any) {
    e.preventDefault()
    if (!editingId) return
    await axios.put('/api/products/update', { id: editingId, name, price, sku })
    setEditingId(null)
    setName('')
    setPrice('0')
    setSku('')
    mutate()
  }

  async function deleteProduct(id: string) {
    if (!confirm('Delete product?')) return
    await axios.delete('/api/products/delete', { data: { id } })
    mutate()
  }

  const products = data?.products || []

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Admin — Products</h2>
      <form onSubmit={create} className="mb-6 max-w-md">
        <input className="w-full p-2 border mb-2" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
        <input className="w-full p-2 border mb-2" placeholder="Price (integer)" value={price} onChange={e => setPrice(e.target.value)} />
        <input className="w-full p-2 border mb-2" placeholder="SKU" value={sku} onChange={e => setSku(e.target.value)} />
        <button className="bg-green-600 text-white px-4 py-2 rounded">Create product</button>
      </form>

      <div className="grid gap-3">
  {products.map((p) => (
          <div key={p.id} className="bg-white p-3 rounded shadow flex justify-between items-center">
            <div>
              <div className="font-semibold">{p.name}</div>
              <div className="text-sm text-gray-500">Price: {p.price}</div>
            </div>
            <div className="space-x-2">
              <button className="text-sm text-blue-600" onClick={() => { setEditingId(p.id); setName(p.name); setPrice(String(p.price)); setSku(p.sku || '') }}>Edit</button>
              <button className="text-sm text-red-600" onClick={() => deleteProduct(p.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

import { useEffect, useState } from 'react'

export default function AdminSettings() {
  const [loading, setLoading] = useState(true)
  const [form, setForm] = useState({ name: '', bankName: '', iban: '', bic: '' })

  useEffect(() => {
    ;(async () => {
      const res = await fetch('/api/admin/bank')
      if (res.ok) {
        const json = await res.json()
        setForm({ name: json.name || '', bankName: json.bankName || '', iban: json.iban || '', bic: json.bic || '' })
      }
      setLoading(false)
    })()
  }, [])

  async function save(e: any) {
    e.preventDefault()
    const res = await fetch('/api/admin/bank', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(form) })
    if (res.ok) alert('Saved')
  }

  if (loading) return <div>Loading...</div>

  return (
    <div className="p-6">
      <h1 className="text-xl font-bold mb-4">Admin settings</h1>
      <form onSubmit={save} className="space-y-3 max-w-lg">
        <div>
          <label className="block text-sm">Account name</label>
          <input value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} className="mt-1 p-2 border rounded w-full" />
        </div>
        <div>
          <label className="block text-sm">Bank name</label>
          <input value={form.bankName} onChange={e => setForm({ ...form, bankName: e.target.value })} className="mt-1 p-2 border rounded w-full" />
        </div>
        <div>
          <label className="block text-sm">IBAN / account</label>
          <input value={form.iban} onChange={e => setForm({ ...form, iban: e.target.value })} className="mt-1 p-2 border rounded w-full" />
        </div>
        <div>
          <label className="block text-sm">BIC / SWIFT (optional)</label>
          <input value={form.bic} onChange={e => setForm({ ...form, bic: e.target.value })} className="mt-1 p-2 border rounded w-full" />
        </div>
        <div>
          <button className="px-4 py-2 bg-blue-600 text-white rounded">Save</button>
        </div>
      </form>
    </div>
  )
}

import type { NextApiRequest, NextApiResponse } from 'next'
import { prisma } from '../../../lib/prisma'
import { encrypt, decrypt } from '../../../lib/crypto'

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  const key = process.env.BANK_DETAILS_KEY
  if (!key) return res.status(500).json({ error: 'encryption key not configured' })

  if (req.method === 'GET') {
    const acc = await prisma.bankAccount.findFirst({})
    if (!acc) return res.status(200).json({})
    const account = { ...acc, iban: decrypt(acc.iban, key) }
    return res.json(account)
  }

  if (req.method === 'POST') {
    const { name, bankName, iban, bic } = req.body
    if (!name || !bankName || !iban) return res.status(400).json({ error: 'missing' })
    const enc = encrypt(iban, key)
    const existing = await prisma.bankAccount.findFirst({})
    if (existing) {
      const updated = await prisma.bankAccount.update({ where: { id: existing.id }, data: { name, bankName, iban: enc, bic } })
      return res.json({ ok: true, id: updated.id })
    }
    const created = await prisma.bankAccount.create({ data: { name, bankName, iban: enc, bic } })
    return res.json({ ok: true, id: created.id })
  }

  return res.status(405).end()
}

