import type { NextApiRequest, NextApiResponse } from 'next'
import { prisma } from '../../../lib/prisma'
import { verifySignature } from '../../../lib/paysera'

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  if (req.method !== 'POST') return res.status(405).end()

  const data = req.body

  const receivedSig = (req.headers['x-paysera-signature'] as string) || req.body.signature || ''
  const signPassword = process.env.PAYSERA_SIGN_PASSWORD || ''
  const algo = (process.env.PAYSERA_SIGN_ALGO as any) || 'hmac-sha256'

  const ok = verifySignature(JSON.stringify(data), receivedSig, signPassword, algo)
  if (!ok) return res.status(400).json({ ok: false, message: 'Invalid signature' })

  const orderId = data.orderId
  if (orderId) {
    try {
      // mark order as paid (or use status from provider payload)
      const status = data.status || 'paid'
      await prisma.order.update({ where: { id: String(orderId) }, data: { status } })
      // create an OrderEvent to record the webhook payload
      await prisma.orderEvent.create({ data: { orderId: String(orderId), event: 'webhook', payload: data } })
    } catch (e) {
      // ignore errors for placeholder
    }
  }

  res.status(200).send('OK')
}

