import { Server } from 'socket.io'
import type { NextApiRequest, NextApiResponse } from 'next'

export default function handler(req: NextApiRequest, res: NextApiResponse) {
  // Next.js runs this file on a per-request basis; we'll attach a socket.io server to the underlying HTTP server.
  const server: any = (res.socket as any).server
  if (!server.io) {
    const io = new Server(server as any, {
      path: '/api/socket'
    })
    server.io = io

    io.on('connection', (socket) => {
      console.log('new socket', (socket as any).id)
      socket.on('message', (msg: unknown) => {
        // Basic runtime check and forward
        if (msg && typeof msg === 'object') {
          io.emit('message', msg)
        }
      })
    })
  }

  res.end()
}

