import { useEffect, useState } from 'react'
import { useRouter } from 'next/router'

export default function PayseraReturn() {
  const router = useRouter()
  const { orderId, status } = router.query
  const [loading, setLoading] = useState(true)
  const [order, setOrder] = useState<any>(null)

  useEffect(() => {
    if (!orderId) return
    // Optionally fetch order status from API
    ;(async () => {
      try {
        const res = await fetch(`/api/paysera/verify?orderId=${encodeURIComponent(String(orderId))}`)
        if (res.ok) {
          const json = await res.json()
          setOrder(json)
        }
      } catch (e) {
        // ignore
      } finally {
        setLoading(false)
      }
    })()
  }, [orderId])

  return (
    <div className="min-h-screen flex items-center justify-center">
      <div className="max-w-xl w-full p-6 bg-white rounded shadow">
        <h1 className="text-2xl font-bold mb-4">Payment result</h1>
        <p className="mb-4">Order: {String(orderId ?? '—')}</p>
        <p className="mb-4">Status (from provider): {String(status ?? 'unknown')}</p>

        {loading ? (
          <p>Checking order status...</p>
        ) : order ? (
          <div>
            <p>Order status: {order.status}</p>
            <pre className="mt-2 text-sm bg-gray-100 p-2 rounded">{JSON.stringify(order, null, 2)}</pre>
          </div>
        ) : (
          <p>No order information available.</p>
        )}
      </div>
    </div>
  )
}

