import React, { useEffect, useState } from 'react'

export default function AdminOrders() {
  const [orders, setOrders] = useState([])
  const [loading, setLoading] = useState(true)
  const adminSecret = import.meta.env.VITE_BACKEND_ADMIN_SECRET || ''
  const apiBase = import.meta.env.VITE_BACKEND_API_URL || ''

  const fetchOrders = async () => {
    setLoading(true)
    try {
      const resp = await fetch(apiBase + '/api/orders', { headers: { ...(adminSecret ? { 'x-admin-secret': adminSecret } : {}) } })
      if (!resp.ok) throw new Error('Failed')
      const data = await resp.json()
      setOrders(data || [])
    } catch (err) {
      console.error(err)
      alert('Failed to load orders (check BACKEND_ADMIN_SECRET and BACKEND_API_URL)')
    } finally { setLoading(false) }
  }

  useEffect(() => { fetchOrders() }, [])

  const updateStatus = async (id, status) => {
    try {
      const resp = await fetch(apiBase + '/api/orders/' + id, { method: 'PATCH', headers: { 'Content-Type': 'application/json', ...(adminSecret ? { 'x-admin-secret': adminSecret } : {}) }, body: JSON.stringify({ status }) })
      if (!resp.ok) throw new Error('update failed')
      await fetchOrders()
    } catch (err) {
      console.error(err)
      alert('Update failed')
    }
  }

  return (
    <div className="page container">
      <h1 className="text-2xl font-semibold mb-4">Admin — Orders</h1>
      {loading ? <div>Loading...</div> : (
        <div className="space-y-3">
          {orders.length === 0 ? <div>No orders</div> : orders.map(o => (
            <div key={o.id} className="bg-white p-4 rounded shadow-sm flex justify-between items-center">
              <div>
                <div className="font-medium">Order {o.id}</div>
                <div className="text-sm text-slate-500">Total: €{o.total}</div>
                <div className="text-sm text-slate-500">Status: {o.status}</div>
              </div>
              <div className="flex gap-2">
                <button className="px-3 py-1 rounded bg-green-600 text-white" onClick={() => updateStatus(o.id, 'paid')}>Mark paid</button>
                <button className="px-3 py-1 rounded bg-yellow-500 text-white" onClick={() => updateStatus(o.id, 'processing')}>Processing</button>
                <button className="px-3 py-1 rounded bg-red-600 text-white" onClick={() => updateStatus(o.id, 'cancelled')}>Cancel</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  )
}

import React, { useState } from 'react'
import { supabase } from '../lib/supabaseClient'

export default function AdminProducts() {
  const [title, setTitle] = useState('')
  const [description, setDescription] = useState('')
  const [price, setPrice] = useState('0.00')
  const [category, setCategory] = useState('merch')
  const [file, setFile] = useState(null)
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState('')

  const uploadAndCreate = async () => {
    setLoading(true)
    setMessage('')
    try {
      let image_url = ''
      if (file) {
        const ext = file.name.split('.').pop()
        const name = `${Date.now()}.${ext}`
        const { error: uploadError } = await supabase.storage.from('media').upload(name, file, { upsert: false })
        if (uploadError) throw uploadError
        const { data } = supabase.storage.from('media').getPublicUrl(name)
        image_url = data.publicUrl
      }

      // call backend to create product
      const apiUrl = (import.meta.env.VITE_BACKEND_API_URL || '') + '/api/shop/products'
      const adminSecret = import.meta.env.VITE_BACKEND_ADMIN_SECRET || ''
      const resp = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', ...(adminSecret ? { 'x-admin-secret': adminSecret } : {}) },
        body: JSON.stringify({ title, description, price, category, image_url })
      })
      if (!resp.ok) throw new Error('Create product failed: ' + (await resp.text()))
      setTitle('')
      setDescription('')
      setPrice('0.00')
      setCategory('merch')
      setFile(null)
      setMessage('Product created')
    } catch (err) {
      console.error(err)
      setMessage(err.message || 'Error')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="page container">
      <h1 className="text-2xl font-semibold mb-4">Admin — Create Product</h1>

      <div className="max-w-xl bg-white p-6 rounded shadow">
        <label className="block mb-2">Title</label>
        <input className="w-full border p-2 rounded mb-3" value={title} onChange={e => setTitle(e.target.value)} />

        <label className="block mb-2">Description</label>
        <textarea className="w-full border p-2 rounded mb-3" value={description} onChange={e => setDescription(e.target.value)} />

        <label className="block mb-2">Price (€)</label>
        <input className="w-full border p-2 rounded mb-3" value={price} onChange={e => setPrice(e.target.value)} />

        <label className="block mb-2">Category</label>
        <input className="w-full border p-2 rounded mb-3" value={category} onChange={e => setCategory(e.target.value)} />

        <label className="block mb-2">Image</label>
        <input type="file" onChange={e => setFile(e.target.files?.[0] ?? null)} className="mb-3" />

        <div className="flex gap-2">
          <button className="rounded bg-indigo-600 text-white px-4 py-2" onClick={uploadAndCreate} disabled={loading}>Create</button>
        </div>

        {message && <div className="mt-3 text-sm">{message}</div>}
      </div>
    </div>
  )
}

import React, { useState, useEffect } from 'react'
import { supabase } from '../lib/supabaseClient'

export default function Auth() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [name, setName] = useState('')
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    const session = supabase.auth.getSession().then(r => setUser(r.data?.session?.user ?? null))
    const { data: listener } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null)
    })
    return () => {
      listener?.subscription?.unsubscribe?.()
    }
  }, [])

  const register = async () => {
    setLoading(true)
    try {
      const { error } = await supabase.auth.signUp({ email, password }, { data: { name } })
      if (error) throw error
      alert('Registration successful — please check your email to confirm (if required).')
    } catch (err) {
      console.error(err)
      alert(err.message || 'Registration error')
    } finally {
      setLoading(false)
    }
  }

  const login = async () => {
    setLoading(true)
    try {
      const { data, error } = await supabase.auth.signInWithPassword({ email, password })
      if (error) throw error
      setUser(data.user || null)
    } catch (err) {
      console.error(err)
      alert(err.message || 'Login error')
    } finally {
      setLoading(false)
    }
  }

  const logout = async () => {
    await supabase.auth.signOut()
    setUser(null)
  }

  return (
    <div className="page auth-page container">
      <h1 className="text-2xl font-semibold mb-4">Auth</h1>

      {user ? (
        <div>
          <div className="mb-4">Signed in as <strong>{user.email}</strong></div>
          <button className="rounded bg-red-500 text-white px-4 py-2" onClick={logout}>Sign out</button>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 max-w-md">
          <input className="border p-2 rounded" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
          <input className="border p-2 rounded" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
          <input className="border p-2 rounded" placeholder="Password" type="password" value={password} onChange={e => setPassword(e.target.value)} />

          <div className="flex gap-2">
            <button className="rounded bg-indigo-600 text-white px-4 py-2" onClick={register} disabled={loading}>Register</button>
            <button className="rounded border px-4 py-2" onClick={login} disabled={loading}>Login</button>
          </div>
