import React, { useEffect, useState } from 'react'
import { FUNCTIONS_URL, supabase } from '../lib/supabaseClient'

export default function Checkout({ onDone }: { onDone?: () => void }) {
  const [items, setItems] = useState<any[]>([])
  const [status, setStatus] = useState<string | null>(null)
  const [userId, setUserId] = useState<string | null>(null)

  useEffect(() => {
    const cart = JSON.parse(localStorage.getItem('cart') || '[]')
    setItems(cart)

    // try to get current user
    (async () => {
      try {
        const { data } = await supabase.auth.getUser()
        setUserId(data?.user?.id || null)
      } catch (e) {
        setUserId(null)
      }
    })()
  }, [])

  const placeOrder = async () => {
    try {
      const total = items.reduce((s: number, it: any) => s + (Number(it.price) || 0) * (it.quantity || 1), 0)
      const payload = { user_id: userId, items, total }
      const resp = await fetch(`${FUNCTIONS_URL}/orders_handler`, {
        method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload)
      })
      const data = await resp.json()
      if (resp.ok) {
        setStatus('Order placed: ' + data.order_id)
        localStorage.removeItem('cart')
        onDone && onDone()
      } else {
        setStatus('Error: ' + JSON.stringify(data))
      }
    } catch (err) {
      setStatus('Error: ' + String(err))
    }
  }

  return (
    <div>
      <h2>Checkout</h2>
      <div>Items: {items.length}</div>
      <div>Total: {items.reduce((s: number, it: any) => s + (Number(it.price)||0)*(it.quantity||1), 0).toFixed(2)}</div>
      <div style={{ marginTop: 8 }}>
        <button onClick={placeOrder}>Place Order</button>
      </div>
      {status && <div style={{ marginTop: 8 }}>{status}</div>}
    </div>
  )
}

import React, { useEffect, useState } from 'react'
import { supabase } from '../lib/supabaseClient'

export default function Feed() {
  const [posts, setPosts] = useState<any[]>([])

  useEffect(() => {
    let mounted = true
    const load = async () => {
      const { data } = await supabase.from('posts').select('*').order('inserted_at', { ascending: false })
      if (mounted && data) setPosts(data as any[])
    }
    load()

    // naive realtime: subscribe to inserts
    const channel = supabase.channel('public:posts')
      .on('postgres_changes', { event: 'INSERT', schema: 'public', table: 'posts' }, (payload) => {
        setPosts((p) => [payload.new, ...p])
      })
      .subscribe()

    return () => {
      mounted = false
      try { supabase.removeChannel(channel) } catch (e) {}
    }
  }, [])

  return (
    <div>
      <h2>Feed</h2>
      {posts.map((p) => (
        <div key={p.id} style={{ borderBottom: '1px solid #ddd', padding: 8 }}>
          <div style={{ fontSize: 12, color: '#666' }}>{p.inserted_at}</div>
          <div>{p.content}</div>
        </div>
      ))}
    </div>
  )
}

