export default function Login({ onSuccess }: { onSuccess?: () => void }) {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [msg, setMsg] = useState<string | null>(null)

  const signUp = async () => {
    const { data, error } = await supabase.auth.signUp({ email, password })
    if (error) setMsg(error.message)
    else setMsg('Check your email to confirm or logged in')
  }

  const signIn = async () => {
    const { data, error } = await supabase.auth.signInWithPassword({ email, password })
    if (error) setMsg(error.message)
    else {
      setMsg('Signed in')
      onSuccess && onSuccess()
    }
  }

  return (
    <div>
      <h2>Login / Sign up</h2>
      <input placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input placeholder="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <div style={{ marginTop: 8 }}>
        <button onClick={signIn}>Sign in</button>
        <button onClick={signUp}>Sign up</button>
      </div>
      {msg && <div style={{ marginTop: 8 }}>{msg}</div>}
    </div>
  )
}

import React, { useEffect, useState } from 'react'
import { supabase } from '../lib/supabaseClient'

export default function Media() {
  const [posts, setPosts] = useState([])
  const [loading, setLoading] = useState(true)
  const [file, setFile] = useState(null)
  const [content, setContent] = useState('')

  useEffect(() => {
    const fetch = async () => {
      setLoading(true)
      try {
        const { data, error } = await supabase.from('posts').select('*').order('created_at', { ascending: false }).limit(50)
        if (error) throw error
        setPosts(data || [])
      } catch (err) {
        console.error('Error fetching posts', err)
      } finally {
        setLoading(false)
      }
    }
    fetch()
  }, [])

  const upload = async () => {
    if (!file) return alert('Choose a file first')
    try {
      const fileExt = file.name.split('.').pop()
      const fileName = `${Date.now()}.${fileExt}`
      const { data: uploadData, error: uploadError } = await supabase.storage.from('media').upload(fileName, file, { cacheControl: '3600', upsert: false })
      if (uploadError) throw uploadError
      // create public URL
      const { data: urlData } = supabase.storage.from('media').getPublicUrl(fileName)
      const mediaUrl = urlData.publicUrl
      // insert post
      const { error: insertError } = await supabase.from('posts').insert([{ content, media_url: mediaUrl }])
      if (insertError) throw insertError
      alert('Uploaded')
      // refresh
      const { data } = await supabase.from('posts').select('*').order('created_at', { ascending: false }).limit(50)
      setPosts(data || [])
      setFile(null)
      setContent('')
    } catch (err) {
      console.error('Upload error', err)
      alert(err.message || 'Upload failed')
    }
  }

  return (
    <div className="page media-page container">
      <h1 className="text-2xl font-semibold mb-4">Медиа: песни, видео, фото, посты</h1>

      <div className="mb-6">
        <textarea className="w-full border p-2 rounded mb-2" placeholder="Текст поста" value={content} onChange={e => setContent(e.target.value)} />
        <input type="file" onChange={e => setFile(e.target.files?.[0] ?? null)} />
        <div className="mt-2">
          <button className="rounded bg-indigo-600 text-white px-4 py-2" onClick={upload}>Upload</button>
        </div>
      </div>

      {loading ? <div>Загрузка...</div> : (
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
          {posts.map(p => (
            <div key={p.id} className="bg-white rounded p-4 shadow-sm">
              <p className="mb-2">{p.content}</p>
              {p.media_url && <img src={p.media_url} alt="media" className="max-w-full rounded" />}
              <div className="mt-2 text-sm text-slate-500">Likes: {p.likes}</div>
            </div>
          ))}
        </div>
      )}

      <p className="mt-6 text-sm text-slate-500">Пользователи смогут лайкать, комментировать и делиться — сейчас реализована базовая загрузка в Supabase Storage (bucket `media`).</p>
    </div>
  )
}

import React, { useEffect, useState, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io();

export default function Messenger() {
  const [room, setRoom] = useState('global');
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const messagesRef = useRef([]);

  useEffect(() => {
    socket.emit('join-room', { room });
    socket.on('chat-message', (m) => {
      messagesRef.current = [...messagesRef.current, m];
      setMessages([...messagesRef.current]);
    });
    return () => { socket.off('chat-message'); };
  }, [room]);

  const send = () => {
    if (!text) return;
    socket.emit('chat-message', { room, message: text });
    setText('');
  };

  return (
    <div className="p-6 page messenger-page">
      <h1 className="text-2xl font-bold">Messenger</h1>
      <p className="mt-2">This is a placeholder page. Realtime chat will be implemented via Socket.IO or Supabase Realtime.</p>
      <div className="mt-4">
        <input className="border p-1" value={room} onChange={e => setRoom(e.target.value)} />
        <button className="ml-2 px-3 py-1 bg-gray-200" onClick={() => socket.emit('join-room', { room })}>Join</button>
      </div>
      <div className="messages mt-4" style={{height:200,overflow:'auto'}}>
        {messages.map((m,i) => <div key={i} className="py-1">{m.id}: {m.message}</div>)}
      </div>
      <div className="mt-3">
        <input className="border p-1" value={text} onChange={e=>setText(e.target.value)} />
        <button className="ml-2 px-3 py-1 bg-blue-600 text-white" onClick={send}>Send</button>
      </div>
    </div>
  );
}

import React, { useEffect, useState } from 'react'
import { supabase } from '../lib/supabaseClient'

export default function Products({ onAddToCart }: { onAddToCart?: () => void }) {
  const [products, setProducts] = useState<any[]>([])

  useEffect(() => {
    const load = async () => {
      const { data } = await supabase.from('products').select('*').order('created_at', { ascending: true })
      if (data) setProducts(data as any[])
    }
    load()
  }, [])

  const add = (p: any) => {
    const existing = JSON.parse(localStorage.getItem('cart') || '[]')
    existing.push({ product_id: p.id, quantity: 1, price: Number(p.price) })
    localStorage.setItem('cart', JSON.stringify(existing))
    onAddToCart && onAddToCart()
  }

  const cart = JSON.parse(localStorage.getItem('cart') || '[]')

  return (
    <div>
      <h2>Products</h2>
      {products.map((p) => (
        <div key={p.id} style={{ borderBottom: '1px solid #eee', padding: 8 }}>
          <div>{p.title} — ${p.price}</div>
          <div style={{ fontSize: 12 }}>{p.description}</div>
          <button onClick={() => add(p)}>Add to cart</button>
        </div>
      ))}
      <div style={{ marginTop: 12 }}>
        <strong>Cart: {cart.length} items</strong>
      </div>
    </div>
  )
}

import React, { useEffect, useState } from 'react';
import { supabase } from '../lib/supabaseClient'
import { useCart } from '../lib/useCart'
import ShoppingCart from '../components/ShoppingCart'

export default function Shop() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const cart = useCart()

  useEffect(() => {
    const fetch = async () => {
      setLoading(true)
      try {
        const { data: prods, error } = await supabase.from('products').select('*').order('created_at', { ascending: false }).limit(100)
        if (error) throw error
        setProducts(prods || [])
        const cats = Array.from(new Set((prods || []).map(p => p.category).filter(Boolean)))
        setCategories(cats)
      } catch (err) {
        console.error('Error fetching products', err)
      } finally {
        setLoading(false)
      }
    }
    fetch()
  }, [])

  return (
    <div className="page shop-page container">
      <h1 className="text-2xl font-semibold mb-4">Магазин</h1>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        <div className="lg:col-span-3">
          <div className="mb-4">
            <h3 className="font-medium">Категории</h3>
            <div className="flex gap-2 mt-2">
              {loading ? <div>Загрузка...</div> : (categories.length ? categories.map(c => <button key={c} className="px-3 py-1 rounded bg-slate-100">{c}</button>) : <div>Нет категорий</div>)}
            </div>
          </div>

          <div>
            <h3 className="font-medium mb-3">Товары</h3>
            {loading ? <div>Загрузка товаров...</div> : (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {products.length ? products.map(p => (
                  <div key={p.id} className="bg-white rounded-lg p-4 shadow-sm flex flex-col">
                    {p.image_url && <img src={p.image_url} alt={p.title} className="w-full h-44 object-cover rounded" />}
                    <div className="mt-2 flex-1">
                      <h4 className="font-semibold">{p.title}</h4>
                      <div className="text-sm text-slate-600">{p.category}</div>
                    </div>
                    <div className="mt-2 flex items-center justify-between">
                      <div className="font-medium">€{p.price}</div>
                      <button className="rounded bg-indigo-600 text-white px-3 py-1" onClick={() => cart.add(p)}>Add</button>
                    </div>
                  </div>
                )) : <div>Нет товаров</div>}
              </div>
            )}
          </div>
        </div>

        <aside>
          <ShoppingCart cart={cart} />
        </aside>
      </div>

      <p className="mt-6 text-sm text-slate-500">Добавлять товары можно через панель администратора (или через SQL insert в Supabase).</p>
    </div>
  )
}

