import { useState } from 'react'
import React from 'react'
import useSWR from 'swr'
import axios from 'axios'

const fetcher = (url: string) => axios.get(url).then(r => r.data)

export default function AdminProducts() {
  const { data, mutate } = useSWR('/api/products', fetcher)
  const [name, setName] = useState('')
  const [price, setPrice] = useState('0')
  const [sku, setSku] = useState('')

  const [editingId, setEditingId] = useState<string | null>(null)

  async function submit(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault()
    if (editingId) {
      await axios.put('/api/products/update', { id: editingId, name, price, sku })
      setEditingId(null)
    } else {
      await axios.post('/api/products/create', { name, price, sku })
    }

    setName('')
    setPrice('0')
    setSku('')
    mutate()
  }

  async function deleteProduct(id: string) {
    if (!confirm('Delete product?')) return
    await axios.delete('/api/products/delete', { data: { id } })
    mutate()
  }

  const products = data?.products || []

  return (
    <div>
      <h2 className="text-2xl font-semibold mb-4">Admin — Products</h2>
  <form onSubmit={submit} className="mb-6 max-w-md">
        <input className="w-full p-2 border mb-2" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
        <input className="w-full p-2 border mb-2" placeholder="Price (integer)" value={price} onChange={e => setPrice(e.target.value)} />
        <input className="w-full p-2 border mb-2" placeholder="SKU" value={sku} onChange={e => setSku(e.target.value)} />
        <button className="bg-green-600 text-white px-4 py-2 rounded">Create product</button>
      </form>

      <div className="grid gap-3">
  {products.map((p) => (
          <div key={p.id} className="bg-white p-3 rounded shadow flex justify-between items-center">
            <div>
              <div className="font-semibold">{p.name}</div>
              <div className="text-sm text-gray-500">Price: {p.price}</div>
            </div>
            <div className="space-x-2">
              <button className="text-sm text-blue-600" onClick={() => { setEditingId(p.id); setName(p.name); setPrice(String(p.price)); setSku(p.sku || '') }}>Edit</button>
              <button className="text-sm text-red-600" onClick={() => deleteProduct(p.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  )
}

