import type { NextApiRequest, NextApiResponse } from 'next'
import { prisma } from '../../../lib/prisma'
import { encrypt, decrypt } from '../../../lib/crypto'

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  const key = process.env.BANK_DETAILS_KEY
  if (!key) return res.status(500).json({ error: 'encryption key not configured' })

  if (req.method === 'GET') {
    const acc = await prisma.bankAccount.findFirst({})
    if (!acc) return res.status(200).json({})
    const account = { ...acc, iban: decrypt(acc.iban, key) }
    return res.json(account)
  }

  if (req.method === 'POST') {
    const { name, bankName, iban, bic } = req.body
    if (!name || !bankName || !iban) return res.status(400).json({ error: 'missing' })
    const enc = encrypt(iban, key)
    const existing = await prisma.bankAccount.findFirst({})
    if (existing) {
      const updated = await prisma.bankAccount.update({ where: { id: existing.id }, data: { name, bankName, iban: enc, bic } })
      return res.json({ ok: true, id: updated.id })
    }
    const created = await prisma.bankAccount.create({ data: { name, bankName, iban: enc, bic } })
    return res.json({ ok: true, id: created.id })
  }

  return res.status(405).end()
}

