import type { NextApiRequest, NextApiResponse } from 'next'

const rateLimitMap: Record<string, number> = {}
const RATE_LIMIT_WINDOW = 30 * 1000 // 30 seconds

async function verifyRecaptcha(token: string | undefined) {
  const secret = process.env.RECAPTCHA_SECRET
  if (!secret) return { ok: true }
  if (!token) return { ok: false, message: 'reCAPTCHA token missing' }

  try {
    const resp = await fetch('https://www.google.com/recaptcha/api/siteverify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: `secret=${encodeURIComponent(secret)}&response=${encodeURIComponent(token)}`
    })
    const data = await resp.json()
    return { ok: data.success, detail: data }
  } catch (err: unknown) {
    // eslint-disable-next-line no-console
    console.error('recaptcha verification error', err)
    return { ok: false, message: 'reCAPTCHA verification failed' }
  }
}

function validEmail(email: string) {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)
}

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  if (req.method !== 'POST') {
    res.setHeader('Allow', ['POST'])
    return res.status(405).json({ ok: false, message: 'Method not allowed' })
  }

  const ip = String(req.headers['x-forwarded-for'] || req.socket.remoteAddress || 'local')
  const last = rateLimitMap[ip]
  const now = Date.now()
  if (last && now - last < RATE_LIMIT_WINDOW) {
    return res.status(429).json({ ok: false, message: 'Too many requests, please wait' })
  }

  const { name, email, message, recaptchaToken } = (req.body || {}) as { name?: string; email?: string; message?: string; recaptchaToken?: string }
  if (!name || !email || !message) {
    return res.status(400).json({ ok: false, message: 'Missing name, email or message' })
  }
  if (!validEmail(email)) {
    return res.status(400).json({ ok: false, message: 'Invalid email' })
  }

  // verify recaptcha if configured
  const recaptcha = await verifyRecaptcha(recaptchaToken)
  if (!recaptcha.ok) {
    return res.status(400).json({ ok: false, message: recaptcha.message || 'reCAPTCHA failed', detail: recaptcha.detail })
  }

  // passed validation and rate-limit; record the attempt
  rateLimitMap[ip] = now

  // In a real app you would validate, persist, or forward this message (email, CRM, etc.)
  // eslint-disable-next-line no-console
  console.log('Contact form submitted:', { name, email, message })

  return res.status(200).json({ ok: true, message: 'Received' })
}

// test helper to reset rate limiter between tests
export function __test_resetRateLimit() {
  for (const k of Object.keys(rateLimitMap)) delete rateLimitMap[k]
}

