  redirectUrl?: string
}

export default async function handler(req: NextApiRequest, res: NextApiResponse) {
  if (req.method !== 'POST') return res.status(405).end()
  const body = req.body as CheckoutBody

  // Create an order with items in the DB
  const total = (body.items || []).reduce((s: number, it: any) => s + (it.price || 0) * (it.quantity || 1), 0)
  const order = await prisma.order.create({
    data: {
      total,
      currency: 'EUR',
      status: 'pending',
      items: {
        create: (body.items || []).map((it: any) => ({ title: it.title || it.name || 'item', price: it.price || 0, quantity: it.quantity || 1, productId: it.id }))
      }
    },
    include: { items: true }
  })

  // Build Paysera params according to their docs. Here we make a placeholder
  const payseraUrl = process.env.PAYSERA_CHECKOUT_URL || ''

  // Example signing (HMAC/MD5) helper is available in lib/paysera
  const signPassword = process.env.PAYSERA_SIGN_PASSWORD || ''
  const algo = (process.env.PAYSERA_SIGN_ALGO as any) || 'hmac-sha256'

  const payloadObj = { orderId: order.id, total: order.total, currency: order.currency, items: order.items }
  const payload = JSON.stringify(payloadObj)
  const signature = signPayload(payload, signPassword, algo)

  // If a Paysera checkout URL is configured, return an auto-submitting HTML form
  // that POSTs the signed payload and signature to the provider (safer than exposing secret on client)
  if (payseraUrl) {
    const body = `<!doctype html><html><head><meta charset="utf-8"><title>Redirecting...</title></head><body>
      <form id="payseraForm" method="POST" action="${payseraUrl}">
        <input type="hidden" name="payload" value='${escapeHtml(payload)}' />
        <input type="hidden" name="signature" value="${escapeHtml(signature)}" />
        <noscript><button type="submit">Continue to payment</button></noscript>
      </form>
      <script>document.getElementById('payseraForm').submit();</script>
    </body></html>`
    res.setHeader('Content-Type', 'text/html; charset=utf-8')
    return res.status(200).send(body)
  }

  // Fallback for local/testing: return JSON with signed payload so frontend can simulate
  res.status(200).json({ ok: true, payUrl: payseraUrl || 'https://paysera.example/checkout-placeholder', signature, payload: payloadObj })
}

function escapeHtml(s: string) {
  return s.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#39;')
}

