        <button className="bg-blue-600 text-white px-4 py-2 rounded" onClick={send}>Send</button>
      </div>
    </div>
  )
}

import React, { useState } from 'react'
import Layout from '../components/Layout'

declare global {
  interface Window {
    __RECAPTCHA_SITE_KEY?: string
    grecaptcha?: { execute: (siteKey: string, opts?: { action?: string }) => Promise<string> }
  }
}

export default function Contact() {
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [message, setMessage] = useState('')
  const [status, setStatus] = useState('')

  async function handleSubmit(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault()
    setStatus('Sending...')
    try {
      let recaptchaToken: string | undefined = undefined
      const siteKey = (window as any).__RECAPTCHA_SITE_KEY
      if (siteKey) {
        // load grecaptcha if not present
        if (!(window as any).grecaptcha) {
          const s = document.createElement('script')
          s.src = `https://www.google.com/recaptcha/api.js?render=${siteKey}`
          document.head.appendChild(s)
          // wait briefly for script to load
          await new Promise((r) => setTimeout(r, 500))
        }
        if (window.grecaptcha) {
          recaptchaToken = await window.grecaptcha.execute(siteKey, { action: 'contact' })
        }
      }
      const res = await fetch('/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, message, recaptchaToken })
      })
      if (res.ok) setStatus('Message sent — thank you!')
      else setStatus('Failed to send message.')
    } catch (err: unknown) {
      // keep the error for diagnostics
      // eslint-disable-next-line no-console
      console.error('contact submit error', err)
      setStatus('Failed to send message.')
    }
  }

  return (
    <Layout>
      <div className="max-w-2xl mx-auto">
        <h1 className="text-3xl font-bold mb-4">Contact Us</h1>

        <p className="text-gray-700 mb-6">Use the form below to send us a message or email us at <a className="text-indigo-600" href="mailto:you@example.com">you@example.com</a>.<br/>Location: City, Country (placeholder)</p>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Name</label>
            <input value={name} onChange={(e) => setName(e.target.value)} className="mt-1 block w-full border rounded px-3 py-2" required />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="mt-1 block w-full border rounded px-3 py-2" required />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Message</label>
            <textarea value={message} onChange={(e) => setMessage(e.target.value)} className="mt-1 block w-full border rounded px-3 py-2" rows={6} required />
          </div>

          <div>
            <button type="submit" className="inline-flex items-center px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700">Send message</button>
          </div>
        </form>

        {status && <p className="mt-4 text-sm text-gray-700">{status}</p>}
      </div>
    </Layout>
  )
}

