  const [loading, setLoading] = useState(true)
  const [order, setOrder] = useState<Record<string, unknown> | null>(null)

  useEffect(() => {
    if (!orderId) return
    // Optionally fetch order status from API
    ;(async () => {
      try {
        const res = await fetch(`/api/paysera/verify?orderId=${encodeURIComponent(String(orderId))}`)
        if (res.ok) {
          const json = await res.json()
          setOrder(json)
        }
      } catch (err: unknown) {
        // log for diagnostics
        // eslint-disable-next-line no-console
        console.warn('paysera return verify failed', err)
      } finally {
        setLoading(false)
      }
    })()
  }, [orderId])

  return (
