import { describe, it, expect, beforeEach, vi } from 'vitest'
import handler, { __test_resetRateLimit } from '../pages/api/contact'

function makeReq(method: string, body?: any, ip = '127.0.0.1') {
  return { method, body, headers: { 'x-forwarded-for': ip } } as any
}

function makeRes() {
  const res: any = {}
  res.status = (code: number) => { res._status = code; return res }
  res.json = (d: any) => { res._json = d; return res }
  res.setHeader = () => {}
  return res
}

describe('contact API', () => {
  beforeEach(() => {
    // reset any mocks
    vi.restoreAllMocks()
  })

  it('accepts POST and returns ok', async () => {
    // if the API tries to verify recaptcha, mock fetch to return success
    vi.stubGlobal('fetch', vi.fn(() => Promise.resolve({ json: () => ({ success: true }) })) as any)

  const req = makeReq('POST', { name: 'A', email: 'a@a.com', message: 'hi', recaptchaToken: 'tok' }, '127.0.0.1')
    const res = makeRes()
    await handler(req, res)
    expect(res._status).toBe(200)
    expect(res._json && res._json.ok).toBe(true)
  })

  it('rejects non-POST methods', async () => {
  const req = makeReq('GET', undefined, '127.0.0.2')
    const res = makeRes()
    await handler(req, res)
    expect(res._status).toBe(405)
  })

  it('returns 400 for missing fields', async () => {
  const req = makeReq('POST', { name: '', email: '', message: '' }, '127.0.0.3')
    const res = makeRes()
    await handler(req, res)
    expect(res._status).toBe(400)
  })

  it('rate limits repeated submissions', async () => {
    vi.stubGlobal('fetch', vi.fn(() => Promise.resolve({ json: () => ({ success: true }) })) as any)
  const req1 = makeReq('POST', { name: 'A', email: 'a@a.com', message: 'hi', recaptchaToken: 'tok' }, '10.0.0.1')
    const res1 = makeRes()
    await handler(req1, res1)
    expect(res1._status).toBe(200)

  const req2 = makeReq('POST', { name: 'A', email: 'a@a.com', message: 'hi', recaptchaToken: 'tok' }, '10.0.0.1')
    const res2 = makeRes()
    await handler(req2, res2)
    // second immediate request should be rate-limited (429)
    expect(res2._status).toBe(429)
  })
})

import { describe, it, expect, vi } from 'vitest'
import { prisma } from '../lib/prisma'
import handler from '../pages/api/paysera/webhook'
import { signPayload } from '../lib/paysera'

function makeReq(body: any, headers: Record<string,string> = {}) {
  return {
    method: 'POST',
    headers,
    body,
    // Next.js uses json body already parsed when content-type application/json
  } as any
}

function makeRes() {
  const res: any = {}
  res.status = (code: number) => { res._status = code; return res }
  res.json = (d: any) => { res._json = d; return res }
  res.send = (d: any) => { res._send = d; return res }
  res.end = () => { return res }
  return res
}

describe('paysera webhook API', () => {
  const payload = { orderId: '42', result: 'ok' }
  const secret = 'test-secret'

  it('returns 200 for valid signature', async () => {
    const sig = signPayload(JSON.stringify(payload), secret, 'hmac-sha256')
    process.env.PAYSERA_SIGN_PASSWORD = secret
    process.env.PAYSERA_SIGN_ALGO = 'hmac-sha256'

    // mock prisma update to avoid DB calls
  vi.spyOn(prisma.product as any, 'update' as any).mockResolvedValue({ id: String(payload.orderId) } as any)

    const req = makeReq(payload, { 'x-paysera-signature': sig })
    const res = makeRes()
    await handler(req, res)
    expect(res._status === 200 || res._send === 'OK').toBeTruthy()
  })

  it('returns 400 for invalid signature', async () => {
    process.env.PAYSERA_SIGN_PASSWORD = secret
    process.env.PAYSERA_SIGN_ALGO = 'hmac-sha256'
    const req = makeReq(payload, { 'x-paysera-signature': 'bad' })
    const res = makeRes()
    await handler(req, res)
    expect(res._status).toBe(400)
  })
})
